import React, { useState } from "react";
// import './index.css'
import {
    ThemeProvider,
    Editable, EditablePreview, EditableInput,
    Modal,
    ModalOverlay,
    ModalContent,
    ModalHeader,
    ModalFooter,
    ModalBody,
    ModalCloseButton,
    useDisclosure,
    FormControl, FormLabel,
    Input,
    Checkbox,
    Button,
		Box,
		toast
} from "@chakra-ui/core";
function Index() {
    const [count, setCount] = useState(""); 
    const [list, setList] = useState([]);
    const [listms, setlTslim] = useState([]);
    console.log(listms);
    //增加页面内容
    const addData = (value) => {
        if (value) {
            setCount("");
            setList([
                ...list,
                value
            ])
        }
    }
    //删除
    function liClick(e) {
        list.splice(e, 1);
        setList([...list]);
    };
    //修改
    function setCounTinp(e){
        let input = document.getElementById("input"+e);
        var value = input.value;
        if (value) {
            list[e] = value;
            setList([...list]);
            input.value="";
        }
      }
    return (
        <Box className="App" w="80%">
					<Button
					onClick={() =>
						toast({
							title: "Account created.",
							description: "We've created your account for you.",
							status: "success",
							duration: 9000,
							isClosable: true,
						})
					}
				>
					Show Toast
				</Button>
				<Input value={count} onChange={(e) => setCount(e.target.value)} />
				<Button onClick={() => addData(count)}>添加</Button>
				<h1>正在进行</h1>
				{
						list.map((item, index) =>
								<Box key={index}>
										<Input id={"input"+index} placeholder={item} size="sm" w="20%" />
										<Checkbox size="md" colorScheme="green" isInvalid onClick={(e) => setlTslim(e)} >
												<span>{item}</span>
										</Checkbox>
										<Button h="1.75rem" size="sm" onClick={() => setCounTinp(index)}>修改</Button>
										<Button h="1.75rem" size="sm" onClick={(e) => liClick(index)}>删除</Button>
								</Box>
						)
				}
				<h1>已经完成</h1>
      </Box>
    );
}
export default Index
