import React from "react";
import {
    ThemeProvider,
    Editable,
    EditablePreview,
    EditableInput,
    Input
} from "@chakra-ui/core";
function Index({ children }) {
    function constructor(props) {
        // super(props);
        this.state = {
            list: []
        }
    };
    addData = () => {
        var templist = this.state.list;
        console.log(templist);
        templist.push(this.refs.title.value);//这个数据的返回值索引值
        //把值赋值给了list
        this.setState({
            list: templist
        })
    };
    handleLogoutClick = () => {
        console.log(this.list);
    }
    return (
        <div className="App">
            <ThemeProvider>
                <div className="input_bot">
                    <Input placeholder="Basic usage" className="input_ls" ref="title" />
                    <Button colorScheme="blue" onClick={this.addData} >按钮</Button>
                </div>
                <h1>正在进行</h1>
                <p id="adc">
                    {
                        this.state.list.map(function (value, key) {
                            return (
                                <p className="vas" key={key}>
                                    <Editable defaultValue={value}>
                                        <EditablePreview></EditablePreview>
                                        <EditableInput></EditableInput>
                                    </Editable>
                                    <Button className="bot" onClick={this.handleLogoutClick()}>删除</Button>
                                </p>
                            )
                        })
                    }
                </p>
                <h1>已经完成</h1>
            </ThemeProvider>
        </div>
    );
}
// const Index = () => (
//     <div>
//         <p>1</p>
//     </div>
// )
export default Index
