import React, { useState } from "react";
// import './index.css'
import {
    ThemeProvider,
    Drawer,
    DrawerBody,
    DrawerFooter,
    DrawerHeader,
    DrawerOverlay,
    DrawerContent,
    DrawerCloseButton,
    useDisclosure,
    Input,
    Button,
    Box
} from "@chakra-ui/core";
function Index() {
    const [count, setCount] = useState("");
    const [list, setList] = useState([]);
    const [listDra, setListDra] = useState([]);

    const {isOpen, onOpen, onClose} = useDisclosure();
    console.log(isOpen, onOpen, onClose);
    console.log(setListDra,listDra);
    const btnRef = React.useRef();

    //增加页面内容
    const addData = (value) => {
        if (value) {
            setCount("");
            setList([
                ...list,
                value
            ])
        }
    }
    //删除
    function liClick(e) {
        console.log(e);
        list.splice(e, 1);
        setList([...list]);
    };
    //修改
    function setCounTinp(e,index) {
        if(e){
            console.log(e,index);
        }
    }
    return (
        <Box className="App">
            <ThemeProvider>
                <Input value={count} onChange={(e) => setCount(e.target.value)} />
                <Button onClick={() => addData(count)}>添加</Button>
                <h1>正在进行</h1>
                <div>
                    {
                        list.map((item, index) =>
                            <Box key={index}>
                                <span ref={btnRef} onClick={onOpen}>{item}</span>
                                <Button h="1.75rem" size="sm" onClick={(e) => liClick(index)}>删除</Button>
                                <Drawer
                                    isOpen={isOpen}
                                    placement="right"
                                    onClose={onClose}
                                    finalFocusRef={btnRef}
                                >
                                    <DrawerOverlay>
                                        <DrawerContent>
                                            <DrawerCloseButton />
                                            <DrawerHeader>修改</DrawerHeader>
                                            <DrawerBody>
                                                <Input onChange={(e) => setListDra(e.target.value)}  />
                                            </DrawerBody>
                                            <DrawerFooter>
                                                <Button variant="outline" mr={3} onClick={onClose}>
                                                    取消
                                                </Button>
                                                <Button color="blue" onClick={(e) => setCounTinp(listDra,index) }>确定</Button>
                                            </DrawerFooter>
                                        </DrawerContent>
                                    </DrawerOverlay>
                                </Drawer>
                            </Box>
                        )
                    }
                </div>
                <h1>已经完成</h1>
            </ThemeProvider>
        </Box>
    );
}
export default Index
