import React,{useState} from "react";
// import './index.css'
import {
    ThemeProvider,
    Editable,
    EditablePreview,
    EditableInput,
    Input,
    Button
} from "@chakra-ui/core";
function Index({ children }) {
    const [count, setCount] = useState(".input_ls");
    const list = []
    function addData() {
        list.push(count.value),count.value
        // console.log(list);
        // var temp = count.value.list;
        // temp.push()
    }
    return (
        <div className="App">
            <ThemeProvider>
                <Input placeholder="Basic usage" className="input_ls" ref={setCount}/>
                <Button colorScheme="blue" onClick={() => addData(count)}>按钮</Button>
                <h1>正在进行</h1>
                <div>
                    {
                        list.map(function (value, key) {
                            return (
                                <p className="vas" key={key}>
                                    <Editable defaultValue={value}>
                                        <EditablePreview></EditablePreview>
                                        <EditableInput></EditableInput>
                                    </Editable>
                                    <Button className="bot">删除</Button>
                                </p>
                            )
                        })
                        
                    }
                </div>
                <h1>已经完成</h1>
            </ThemeProvider>
        </div>
    );
}
// const Index = () => (
//     <div>
//         <p>1</p>
//     </div>
// )
export default Index
