import React, { useState } from "react";
// import './index.css'
import {
    ThemeProvider,
    Editable, EditablePreview, EditableInput,
    Modal,
    ModalOverlay,
    ModalContent,
    ModalHeader,
    ModalFooter,
    ModalBody,
    ModalCloseButton,
    useDisclosure,
    FormControl, FormLabel,
    Input,
    Button,
    Box
} from "@chakra-ui/core";
// function dials() {
