//                     <FormControl>
//                         <FormLabel>内容</FormLabel>
//                         <Input ref={initialRef} placeholder={item} />
//                     </FormControl>
//                 </ModalBody>
//                 <ModalFooter>
//                     <Button onClick={onClose}>取消</Button>
//                     <Button colorScheme="blue" mr={3}>
//                         确定
//                     </Button>
//                 </ModalFooter>
//             </ModalContent>
//         </Modal>
//     )
// }
function Index() {
    const [count, setCount] = useState("");
    const [list, setList] = useState([]);
    const [listDra, setListDra] = useState([]);
    // const { isOpen, onOpen, onClose } = useDisclosure();
    // const initialRef = React.useRef();
    // const finalRef = React.useRef();
    // console.log(initialRef, finalRef);
    //增加页面内容
    const addData = (value) => {
        if (value) {
            setCount("");
            setList([
                ...list,
                value
            ])
        }
    }
    //删除
    function liClick(e) {
        list.splice(e, 1);
        setList([...list]);
    };
    //修改
    function setCounTinp(e) {
        console.log(e);
        // if (e) {
        //     console.log(e);
        // }
    }
    return (
        <Box className="App">
            <ThemeProvider>
                <Input value={count} onChange={(e) => setCount(e.target.value)} />
                <Button onClick={() => addData(count)}>添加</Button>
                <h1>正在进行</h1>
                {
                    list.map((item, index) =>
                        <Box key={index}>
                            <Input placeholder="small size" size="lg" />
                            <span>{item}</span>
                            <Button h="1.75rem" size="sm" onClick={(e) => setCounTinp(index)}>修改</Button>
                            <Button h="1.75rem" size="sm" onClick={(e) => liClick(index)}>删除</Button>
                        </Box>
                    )
                }
                <h1>已经完成</h1>
            </ThemeProvider>
        </Box>
    );
}
export default Index
