import React, { useState } from "react";
// import './index.css'
import {
    ThemeProvider,
    Editable, EditableInput, EditablePreview,
    Flex,
    ButtonGroup,
    IconButton,
    EditableControls,
    Input,
    Button,
    Box
} from "@chakra-ui/core";
function Index() {
    const [count, setCount] = useState("");
    const [list, setList] = useState([]);
    const [listDra, setListDra] = useState([]);

    function EditableControls({ isEditing, onSubmit, onCancel, onEdit }) {
        return isEditing ? (
          <ButtonGroup justifyContent="center" size="sm">
            <IconButton icon={<CheckIcon />} onClick={onSubmit} />
            <IconButton icon={<CloseIcon />} onClick={onCancel} />
          </ButtonGroup>
        ) : (
          <Flex justifyContent="center">
            <IconButton size="sm" icon={<EditIcon />} onClick={onEdit} />
          </Flex>
        )
      }

    //增加页面内容
    const addData = (value) => {
        if (value) {
            setCount("");
            setList([
                ...list,
                value
            ])
        }
    }
    //删除
    function liClick(e) {
        console.log(e);
        list.splice(e, 1);
        setList([...list]);
    };
    //修改
    function setCounTinp(e, index) {
        if (e) {
            console.log(e, index);
        }
    }
    return (
        <Box className="App">
            <ThemeProvider>
                <Input value={count} onChange={(e) => setCount(e.target.value)} />
                <Button onClick={() => addData(count)}>添加</Button>
                <h1>正在进行</h1>
                <div>
                    {
                        list.map((item, index) =>
                            <Box key={index}>
                                <span>{item}</span>
                                <Button h="1.75rem" size="sm" onClick={(e) => liClick(index)}>删除</Button>
                                <Editable
                                    textAlign="center"
                                    defaultValue="Rasengan ⚡️"
                                    fontSize="2xl"
                                    isPreviewFocusable={false}
                                    submitOnBlur={false}
                                    >
                                    {(props) => (
                                        <>
                                        <EditablePreview />
                                        <EditableInput />
                                        <EditableControls {...props} />
                                        </>
                                    )}
                                    </Editable>
                            </Box>
                        )
                    }
                </div>
                <h1>已经完成</h1>
            </ThemeProvider>
        </Box>
    );
}
export default Index
