import React, { useState } from "react";
// import './index.css'
import {
    ThemeProvider,
    Editable, EditablePreview, EditableInput,
    Modal,
    ModalOverlay,
    ModalContent,
    ModalHeader,
    ModalFooter,
    ModalBody,
    ModalCloseButton,
    useDisclosure,
    FormControl, FormLabel,
    Input,
    Checkbox,
    Button,
    Box,
    CSSReset,
    theme
} from "@chakra-ui/core";
import Todolist from './todolist'
function Index() {
    const [count, setCount] = useState(""); 
    const [list, setList] = useState([]);
    const [listms, setlTslim] = useState([]);
    console.log(listms);
    //增加页面内容
    const addData = (value) => {
        if (value) {
            setCount("");
            setList([
                ...list,
                value
            ])
        }
    }
    //删除
    function liClick(e) {
        list.splice(e, 1);
        setList([...list]);
    };
    //修改
    function setCounTinp(e){
        let input = document.getElementById("input"+e);
        var value = input.value;
        if (value) {
            list[e] = value;
            setList([...list]);
            input.value="";
        }
      }
    return (
            <ThemeProvider theme={theme}>
                <CSSReset/>
       <Todolist/>
            </ThemeProvider>
    );
}
export default Index
