import React, { useState } from "react";
// import './index.css'
import {
    ThemeProvider,
    Editable, EditablePreview, EditableInput,
    Modal,
    ModalOverlay,
    ModalContent,
    ModalHeader,
    ModalFooter,
    ModalBody,
    ModalCloseButton,
    useDisclosure,
    FormControl, FormLabel,
    Input,
    Checkbox,
    Button,
    Box
} from "@chakra-ui/core";
// function dials() {
//     const { isOpen, onOpen, onClose } = useDisclosure();
//     const initialRef = React.useRef();
//     const finalRef = React.useRef();
//     console.log(initialRef, finalRef);
//     return (
//         <Modal
//             initialFocusRef={initialRef}
//             finalFocusRef={finalRef}
//             isOpen={isOpen}
//             onClose={onClose}
//         >
//             <ModalOverlay />
//             <ModalContent>
//                 <ModalHeader>Create your account</ModalHeader>
//                 <ModalCloseButton />
//                 <ModalBody pb={6}>
//                     <FormControl>
//                         <FormLabel>内容</FormLabel>
//                         <Input ref={initialRef} placeholder={item} />
//                     </FormControl>
//                 </ModalBody>
//                 <ModalFooter>
//                     <Button onClick={onClose}>取消</Button>
//                     <Button colorScheme="blue" mr={3}>
//                         确定
//                     </Button>
//                 </ModalFooter>
//             </ModalContent>
//         </Modal>
//     )
// }
function Index() {
    const [count, setCount] = useState(""); //添加
    const [list, setList] = useState([]);   //渲染
    const [listDra, setListDra] = useState([]);
    //增加页面内容
    const addData = (value) => {
        console.log(value);
        if (value) {
            setCount("");
            setList([
                ...list,
                value
            ])
            console.log(...list);
        }
    }
    //删除
    function liClick(e) {
        list.splice(e, 1);
        setList([...list]);
    };
    //修改
    // function setCounTinp(e) {
    //     setListDra([...listDra]);
    //     console.log(e,listDra);
    //     list.map((item,index) => {
    //         console.log(item,index);
    //         if (index === e) {
    //             item === listDra
    //         }
    //     })
    // }
    function EditToDo(e,id){
        console.log(id)
        let inputEl = document.getElementById("input"+id)
        var value = inputEl.value;
        if (value == "") {
          alert("修改内容不能为空"); 
          return false
        }
        // var tempe = list;
        list[id] = value;
        // console.log(tempe[id]);
        setList([...list]);
        // ToDoText.target.value="";  
        inputEl.value="";
      }
    return (
        <Box className="App" w="80%">
            <ThemeProvider>
                <Input value={count} onChange={(e) => setCount(e.target.value)} />
                <Button onClick={() => addData(count)}>添加</Button>
                <h1>正在进行</h1>
                {
                    list.map((item, index) =>
                        <Box key={index}>
                            <Input id={"input"+index} placeholder={item} size="sm" w="20%" />
                            <Checkbox size="md" colorScheme="green" isInvalid onClick={(e) => checkbox(e)} >
                                <span>{item}</span>
                            </Checkbox>
                            <Button h="1.75rem" size="sm" id={"btn"+index} onClick={EditToDo.bind(this,"",index)}>修改</Button>
                            <Button h="1.75rem" size="sm" onClick={(e) => liClick(index)}>删除</Button>
                        </Box>
                    )
                }
                <h1>已经完成</h1>
            </ThemeProvider>
        </Box>
    );
}
export default Index
