import React,{useState} from "react";
// import './index.css'
import {
    ThemeProvider,
    Editable,
    EditablePreview,
    EditableInput,
    Input,
    Button,
    Box
} from "@chakra-ui/core";
function Index({ children }) {
    const [count, setCount] = useState();
    // console.log(count, setCount)
    let [list, setList] = useState([]);
    //增加页面内容
    function addData(value) {
        setList([
            ...list,
            value
        ])
        value = ""
    }
    //删除页面内容。
    function addbutton(key) {
        // list.splice(key,1);
        console.log(list[key],key);
    }
    return (
        <Box className="App">
            <ThemeProvider>
                <Input placeholder="Basic usage" className="input_ls" onChange={(value) => setCount(value.target.value)}/>
                <Button colorScheme="blue" onClick={() => addData(count)}>按钮</Button>
                <h1>正在进行</h1>
                <div>
                    {
                        list.map(function (value, key) {
                            return (
                                <div className="vas" key={key}>
                                    <Editable defaultValue={value}>
                                        <EditablePreview></EditablePreview>
                                        <EditableInput></EditableInput>
                                    </Editable>
                                    <Button className="bot" onClick={() => addbutton(key)}>删除</Button>
                                </div>
                            )
                        })
                    }
                </div>
                <h1>已经完成</h1>
            </ThemeProvider>
        </Box>
    );
}
export default Index
