//                     </FormControl>
//                 </ModalBody>
//                 <ModalFooter>
//                     <Button onClick={onClose}>取消</Button>
//                     <Button colorScheme="blue" mr={3}>
//                         确定
//                     </Button>
//                 </ModalFooter>
//             </ModalContent>
//         </Modal>
//     )
// }
function Index() {
    const [count, setCount] = useState(""); //添加
    const [list, setList] = useState([]);   //渲染
    const [listDra, setListDra] = useState([]);
    //增加页面内容
    const addData = (value) => {
        console.log(value);
        if (value) {
            setCount("");
            setList([
                ...list,
                value
            ])
            console.log(...list);
        }
    }
    //删除
    function liClick(e) {
        list.splice(e, 1);
        setList([...list]);
    };
    //修改
    // function setCounTinp(e) {
    //     setListDra([...listDra]);
    //     console.log(e,listDra);
    //     list.map((item,index) => {
    //         console.log(item,index);
    //         if (index === e) {
    //             item === listDra
    //         }
    //     })
    // }
    function setCounTinp(e,id){
        console.log(e,id)
        let inputEl = document.getElementById("input"+e);
        console.log(inputEl);
        // var value = inputEl.value;
        // if (value == "") {
        //   return false
        // }
        // list[e] = value;
        // setList([...list]);
        // inputEl.value="";
      }
    return (
        <Box className="App" w="80%">
            <ThemeProvider>
                <Input value={count} onChange={(e) => setCount(e.target.value)} />
                <Button onClick={() => addData(count)}>添加</Button>
                <h1>正在进行</h1>
                {
                    list.map((item, index) =>
                        <Box key={index}>
                            <Input id={"input"+index} placeholder={item} size="sm" w="20%" />
                            <Checkbox size="md" colorScheme="green" isInvalid onClick={(e) => checkbox(e)} >
                                <span>{item}</span>
                            </Checkbox>
                            <Button h="1.75rem" size="sm" id={"btn"+index} onClick={() => setCounTinp("",index)}>修改</Button>
                            <Button h="1.75rem" size="sm" onClick={(e) => liClick(index)}>删除</Button>
                        </Box>
                    )
                }
                <h1>已经完成</h1>
            </ThemeProvider>
        </Box>
    );
}
export default Index
