import React, { useState } from "react";
// import './index.css'
import {
    ThemeProvider,
    CSSReset,
    theme
} from "@chakra-ui/core";
import Todolist from './todolist'
function Index() {
    const [todos, setTodos] = useState([]);
    function addTodo(val) {
        setTodos([
            ...todos,{
                id: Date.now(),
                val,
                completed: false
            }
        ])
    }
    return (
        <ThemeProvider theme={theme}>
            <CSSReset/>
            <Todolist todos = {todos}/>
        </ThemeProvider>
    );
}
export default Index
