import React,{useState} from "react";
// import './index.css'
import {
    ThemeProvider,
    Editable,
    EditablePreview,
    EditableInput,
    Input,
    Button
} from "@chakra-ui/core";
function Index({ children }) {
    const [count, setCount] = useState();
    let list = [
        "4864684"
    ]
    setState = (updater, [callback]) => {
        console.log(updater,callback)
    }
    function addData() {
        // this.setState((state, props) => {
        //     console.log(state,props);
        // })
        list.push(count.value);
        console.log(list);
    }
    function addbutt(e) {
        console.log('删除！',e);
    }
    return (
        <div className="App">
            <ThemeProvider>
                <Input placeholder="Basic usage" className="input_ls" ref={setCount}/>
                <Button colorScheme="blue" onClick={() => addData(count)}>按钮</Button>
                <h1>正在进行</h1>
                <div>
                    {
                        list.map(function (value, key) {
                            return (
                                <div className="vas" key={key}>
                                    <Editable defaultValue={value}>
                                        <EditablePreview></EditablePreview>
                                        <EditableInput></EditableInput>
                                    </Editable>
                                    <Button className="bot" onClick={addbutt(key)}>删除</Button>
                                </div>
                            )
                        })
                        
                    }
                </div>
                <h1>已经完成</h1>
            </ThemeProvider>
        </div>
    );
}
// const Index = () => (
//     <div>
//         <p>1</p>
//     </div>
// )
export default Index
